import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Modal, Button } from 'react-bootstrap'
import { withFormik, Form, Field, Formik } from 'formik';

function MyModal({open,handleClose,boarD}) {

const handleSubmit = (values) => {

    const REST_API_URL = "http://127.0.0.1:8000/api/board/";
    fetch(REST_API_URL, {
      method: 'path',
      body: JSON.stringify(values),
      headers: {
                'Content-Type': 'application/json',
                }
    }).then(response=> {
      if (response.ok) {
        return response.json();
      } else {
        // HANDLE ERROR
        throw new Error('Something went wrong');
      }
    }).then(data => {
      // HANDLE RESPONSE DATA
      console.log(data);
      props.history.push("/")
    }).catch((error) => {
      // HANDLE ERROR
    });
  }
  return (
    <div>


      <Modal show={open} onHide={handleClose}>

        <Modal.Header closeButton>
          <Modal.Title> title id board</Modal.Title>
        </Modal.Header>

        <Modal.Body>
     <Formik
       initialValues={{ name: boarD.name, description: boarD.description }}
       onSubmit={handleSubmit}
       validationSchema={validationSchema}
       enableReinitialize
     >
       {boarD => (
          <Form className="form-container">
            <div className="form-group">
              <label htmlFor="username">name</label>
              <Field type="text" name="username" id={"name"} className={"form-control "} placeholder="name" />
              { boarD.touched.name && boarD.errors.name && <span className="help-block text-danger">{boarD.errors.name}</span> }
            </div>
              <div className="form-group">
              <label htmlFor="email">Description</label>
              <Field type="text" name="description" id={"description"} className={"form-control"} placeholder="Description" />
              { boarD.touched.description && boarD.errors.description && <span className="help-block text-danger">{boarD.errors.description}</span> }
            </div>
          </Form>)}
             </Formik>
        </Modal.Body>

        <Modal.Footer>

          <Button variant="secondary" onClick={handleClose}>Close</Button>
          <Button variant="submit" >Submit</Button>

        </Modal.Footer>

      </Modal>

    </div>
  );
}

export default MyModal;
