import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
import {Link} from 'react-router-dom';
import axios from 'axios';


function Table() {
  const [boards, SetBoards] = useState([])
   useEffect(() => {

        axios({
            method: "GET",
            url: `http://127.0.0.1:8000/api/board/`
        }).then(response => {
            SetBoards(response.data)
        })
    }, [])


  return (
    <div className="App">

     <table className="table infinite-container" id="board-table">
        <thead className="thead-dark">
        <tr>
          <th>Board</th>
          <th>Posts</th>
          <th>Topics</th>
          <th>Last Post</th>

          <th>Edit</th>

        </tr>
        </thead>
       {boards.map(b => (
        <tbody className="infinite-item" key={b.id}>

        <tr>
          <td>
           <p>{b.id}</p> <small className="text-muted d-block"> {b.name} </small>
          </td>
          <td className="align-middle">
            {b.topics_count}
          </td>
          <td className="align-middle">
            {b.posts_count}
          </td>

          <td className="align-middle">

            <small>
                {b.last_post}
              <a href="">
                By  at
              </a>
            </small>
          </td>


          <td>

          </td>

        </tr>

        </tbody>
           ))}
      </table>


    </div>
  );
}

export default Table;
