import React, { useState, useEffect } from 'react';
import axios from "axios";
import {Link} from "react-router-dom";

function TopicDetail({ match }) {

    const [topic, SetTopic] = useState({})
    const [posts, SetPosts] = useState([])
    const id = match.params.id
    const pk = match.params.pk
    console.log("match", match)
    useEffect(() => {

        axios({
            method: "GET",
            url: `http://127.0.0.1:8000/api/board/${id}/topic/${pk}/`
        }).then(response => {
            SetTopic(response.data)
            SetPosts(response.data.posts)
        })
    }, [id])

    return(

<div className="App bordered m-2 mt-5">
       {posts.map(p => (

           <div key="{p.id}" className="card m-4">

               <div className="card-header text-white bg-dark py-2 px-3 ">{topic.subject}</div>

               <div className="card-body p-3">
                   <div className="row">
                       <div className="col-2">
                            <small>Posts: user`s</small>
                       </div>
                       <div className="col-10">
                           <div className="row mb-3">
                               <div className="col-6">
                                   <a href=""><strong
                                       className="text-muted">{p.created_by}</strong></a>
                               </div>
                               <div className="col-6 text-right">
                                   <small className="text-muted">{p.created_at}</small>
                               </div>
                           </div>
                           {p.message}
                       </div>
                   </div>
               </div>
           </div>

       ))}
</div>

    )
}

export default TopicDetail;



