import React, {useState, useCallback, useRef, useEffect} from "react";
import styled from "styled-components";
import ReactCrop from "react-image-crop";
import "react-image-crop/dist/ReactCrop.css";
import Uploady, {
  withRequestPreSendUpdate,
  useItemFinalizeListener
} from "@rpldy/uploady";
import { getMockSenderEnhancer } from "@rpldy/mock-sender";
import UploadButton from "@rpldy/upload-button";
import UploadPreview, { PREVIEW_TYPES } from "@rpldy/upload-preview";
import cropImage from "./crop";
import "./style.css";
import Profile from "./my_account";
import axios from "axios";
import FormData from "form-data";



const StyledReactCrop = styled(ReactCrop)`
  width: 100%;
  max-width: 900px;
  height: 400px;
`;

const PreviewImage = styled.img`
  margin: 5px;
  max-width: 200px;
  height: auto;
  max-height: 200px;
`;

const ButtonsWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-top: 10px;
`;
const tokens = localStorage.tokens;

const handleSubmit = (values) => {
    let data = new FormData();
    for(const file of values.file){
        data.append("image", file);
    }
     // data.append('file[]', values.file, values.file.name,);
     // data.append('subject', values.subject);
    console.log("data", data)
    console.log("values", values.file.name)
    const REST_API_URL = `http://127.0.0.1:8000/api/account/`;
    axios.post(REST_API_URL, data, {
  headers: {
    'accept': 'application/json',
    'Accept-Language': 'en-US,en;q=0.8',
    'Content-Type': `multipart/form-data`,
    'Authorization': `Token ${tokens}`,
  }
})
  .then((response) => {
      if (response.status===201) {
          alert("Success");
            // handleCloseTopic()
      }
      else {
          alert("error");
          // handleCloseTopic()
          // window.location.reload();
        throw new Error('Something went wrong');
      }
  });
  }





const PreviewButtons = (props) => {

  const { finished, crop, updateRequest, onUploadCancel, onUploadCrop } = props;

  return (

      <div className="container mt-5">
      <div className="page-content page-container ml-4 " id="page-content" key={user.id}>
          <div className="padding mb-5">
              <div className="row container d-flex justify-content-center mb-5">
                  <div className="col-xl-6 col-md-12">
                      <div className="card user-card-full">
                          <div className="row m-l-0 m-r-0">
                              <div className="col-sm-4 bg-c-lite-green user-profile">
                                  <div className="card-block text-center text-white">
                                      <div className="m-b-5">
                                          <img
                                          src="https://img.icons8.com/bubbles/100/000000/user.png"
                                          className="img-radius" alt="User-Profile-Image" />
                                      </div>
                                          <ButtonsWrapper>
      <button
        style={{
          display: !finished && updateRequest && crop ? "block" : "none"
        }}
        onClick={onUploadCrop}
      >
        Upload Cropped
      </button>
      <button
        style={{ display: !finished && updateRequest ? "block" : "none" }}
        onClick={updateRequest}
      >
        Upload without Crop
      </button>
      <button
        style={{
          display: !finished && updateRequest && crop ? "block" : "none"
        }}
        onClick={handleSubmit}
      >
        Cancel
      </button>
    </ButtonsWrapper>
                                      <h6 className="f-w-600"> </h6>
                                      <p>Web </p> <i
                                      className=" mdi mdi-square-edit-outline feather icon-edit m-t-10 f-16"> </i>
                                  </div>
                              </div>
                              <div className="col-sm-8">
                                  <div className="card-block">
                                      <h6 className="m-b-20 p-b-5 b-b-default f-w-600 mb-5">Information</h6>
                                      <div className="row">
                                          <div className="col-sm-6">
                                              <p className="m-b-10 f-w-600">Username</p>
                                              <h6 className="text-muted f-w-400">{user.username} </h6>
                                          </div>
                                          <div className="col-sm-6">
                                              <p className="m-b-10 f-w-600">Email</p>
                                              <h6 className="text-muted f-w-400">{user.email} </h6>
                                          </div>
                                      </div>
                                  </div>
                              </div>
                          </div>
                      </div>
                  </div>
              </div>
          </div>
      </div>
    </div>












  );
};

const ItemPreviewWithCrop = withRequestPreSendUpdate((props) => {
  const {
    id,
    url,
    isFallback,
    type,
    updateRequest,
    requestData,
    previewMethods
  } = props;
  const [finished, setFinished] = useState(false);
  const [crop, setCrop] = useState(null);

  useItemFinalizeListener(() => {
    setFinished(true);
  }, id);

  const onUploadCrop = useCallback(async () => {
    if (updateRequest && (crop?.height || crop?.width)) {
      requestData.items[0].file = await cropImage(
        url,
        requestData.items[0].file,
        crop
      );
      updateRequest({ items: requestData.items });
    }
  }, [url, requestData, updateRequest, crop]);

  const onUploadCancel = useCallback(() => {
    updateRequest(false);
    if (previewMethods.current?.clear) {
      previewMethods.current.clear();
    }
  }, [updateRequest, previewMethods]);

  return isFallback || type !== PREVIEW_TYPES.IMAGE ? (
    <PreviewImage src={url} alt="fallback img" />
  ) : (
    <>
      {requestData && !finished ? (
        <StyledReactCrop
          src={url}
          crop={crop}
          onChange={setCrop}
          onComplete={setCrop}
        />
      ) : (
        <PreviewImage src={url} alt="uploading img" />
      )}
      <PreviewButtons
        finished={finished}
        crop={crop}
        updateRequest={updateRequest}
        onUploadCancel={onUploadCancel}
        onUploadCrop={onUploadCrop}
      />
      <p>{finished ? "FINISHED" : ""}</p>
    </>
  );
});

const mockSenderEnhancer = getMockSenderEnhancer({ delay: 1500 });

export default function Profile2() {
  const previewMethodsRef = useRef();

  return (
    <Uploady
      destination={{ url: "[upload-url]" }}
      enhancer={mockSenderEnhancer}
    >
      <div className="App">
        <h1>Hello React Uploady</h1>
        <UploadButton>Upload Files</UploadButton>
        <br />
        <UploadPreview
          PreviewComponent={ItemPreviewWithCrop}
          previewComponentProps={{ previewMethods: previewMethodsRef }}
          previewMethodsRef={previewMethodsRef}
          fallbackUrl="https://icon-library.net/images/image-placeholder-icon/image-placeholder-icon-6.jpg"
        />
      </div>
    </Uploady>
  );
}
